          onClick={() => setCurrentTab('routine')}
          className={`flex-1 py-3 flex flex-col items-center space-y-1 transition-colors ${
            currentTab === 'routine' ? 'text-blue-500' : 'text-gray-400'
          }`}
        >
          <RotateCcw className="w-4 h-4" />
          <span className="text-xs font-medium">Routine Design</span>
        </button>
        <button
          onClick={() => setCurrentTab('workout')}
          className={`flex-1 py-3 flex flex-col items-center space-y-1 transition-colors ${
            currentTab === 'workout' ? 'text-blue-500' : 'text-gray-400'
          }`}
        >
          <Target className="w-4 h-4" />
          <span className="text-xs font-medium">Workout Design</span>
        </button>
        <button
          onClick={() => setCurrentTab('weekly')}
          className={`flex-1 py-3 flex flex-col items-center space-y-1 transition-colors ${
            currentTab === 'weekly' ? 'text-blue-500' : 'text-gray-400'
          }`}
        >
          <BarChart3 className="w-4 h-4" />
          <span className="text-xs font-medium">Weekly</span>
        </button>
      </div>

      {showExerciseLibrary && (
        <div className="fixed inset-0 bg-black/40 backdrop-blur-sm z-50 flex items-end">
          <div className="bg-white w-full h-4/5 rounded-t-3xl flex flex-col shadow-2xl">
            <div className="p-6 border-b border-gray-100 flex items-center justify-between">
              <h2 className="text-xl font-bold text-gray-900">
                {libraryMode === 'exercises' ? 'Exercise Library' : 'Copy from Previous Days'}
              </h2>
              <button
                onClick={() => {
                  setShowExerciseLibrary(false);
                  setLibraryMode('exercises');
                }}
                className="w-8 h-8 bg-gray-100 hover:bg-gray-200 rounded-full flex items-center justify-center transition-colors"
              >
                <X className="w-4 h-4 text-gray-600" />
              </button>
            </div>

            <div className="p-4 border-b border-gray-100">
              <div className="flex space-x-2">
                <button
                  onClick={() => setLibraryMode('exercises')}
                  className={`flex-1 py-2 px-4 rounded-xl text-sm font-medium transition-all ${
                    libraryMode === 'exercises'
                      ? 'bg-blue-500 text-white'
                      : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                  }`}
                >
                  Add New Exercises
                </button>
                <button
                  onClick={() => setLibraryMode('copy')}
                  className={`flex-1 py-2 px-4 rounded-xl text-sm font-medium transition-all ${
                    libraryMode === 'copy'
                      ? 'bg-green-500 text-white'
                      : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                  }`}
                >
                  Copy from Past Days
                </button>
              </div>
            </div>

            <div className="flex-1 overflow-y-auto p-6">
              {libraryMode === 'exercises' ? (
                <div className="space-y-4">
                  {Object.entries(exerciseLibraryByMuscle).map(([muscleGroup, exercises]) => {
                    const filteredExercises = exercises.filter(exercise => 
                      exercise.name.toLowerCase().includes(searchTerm.toLowerCase())
                    );

                    if (filteredExercises.length === 0) return null;

                    const isCollapsed = collapsedMuscleGroups[muscleGroup];
                    const muscleColors = {
                      'Chest': 'bg-red-500',
                      'Triceps': 'bg-orange-500',
                      'Shoulders': 'bg-yellow-500',
                      'Legs': 'bg-green-500',
                      'Back': 'bg-blue-500',
                      'Abs': 'bg-purple-500',
                      'Biceps': 'bg-teal-500',
                      'Custom': 'bg-pink-500'
                    };

                    return (
                      <div key={muscleGroup} className="bg-gray-50 rounded-xl overflow-hidden">
                        <button
                          onClick={() => toggleMuscleGroupCollapse(muscleGroup)}
                          className="w-full p-4 flex items-center justify-between hover:bg-gray-100 transition-colors"
                        >
                          <div className="flex items-center space-x-3">
                            <div className={`w-4 h-4 rounded-full ${muscleColors[muscleGroup] || 'bg-gray-400'}`}></div>
                            <h3 className="text-lg font-semibold text-gray-900">{muscleGroup}</h3>
                            <span className="text-sm text-gray-500">({filteredExercises.length} exercises)</span>
                          </div>
                          <ChevronDown className={`w-5 h-5 text-gray-400 transition-transform ${!isCollapsed ? 'rotate-180' : ''}`} />
                        </button>

                        {!isCollapsed && (
                          <div className="border-t border-gray-200 p-4 space-y-3">
                            {filteredExercises.map(exercise => (
                              <div
                                key={exercise.id}
                                className="bg-white rounded-lg p-3 flex items-center justify-between hover:bg-gray-50 transition-colors"
                              >
                                <div className="flex-1">
                                  <div className="font-medium text-gray-900">{exercise.name}</div>
                                  {exercise.muscleWeights && (
                                    <div className="mt-2 mb-1">
                                      <MuscleGroupBar 
                                        muscleGroups={exercise.muscleGroups} 
                                        muscleWeights={exercise.muscleWeights} 
                                      />
                                    </div>
                                  )}
                                  <div className="text-sm text-gray-500 mt-1">{exercise.muscleGroups.join(', ')}</div>
                                </div>
                                <button
                                  onClick={() => addExerciseDirectly(exercise)}
                                  className="ml-4 w-8 h-8 bg-blue-500 text-white rounded-full flex items-center justify-center hover:bg-blue-600 transition-colors shadow-md"
                                >
                                  <Plus className="w-4 h-4" />
                                </button>
                              </div>
                            ))}
                          </div>
                        )}
                      </div>
                    );
                  })}
                </div>
              ) : (
                <div className="space-y-4">
                  {Array.from({length: 10}, (_, weekIndex) => (
                    <div key={weekIndex} className="bg-gray-50 rounded-xl p-4">
                      <h3 className="text-md font-semibold text-gray-900 mb-3">Week {weekIndex + 1}</h3>
                      <div className="space-y-2">
                        {days.map((day, dayIndex) => {
                          const dayPlan = workoutPlans[weekIndex][dayIndex];
                          const isCurrentDay = weekIndex === currentWeek && dayIndex === currentDay;

                          return (
                            <div
                              key={dayIndex}
                              className={`p-3 rounded-lg flex items-center justify-between ${
                                isCurrentDay 
                                  ? 'bg-gray-300 border-2 border-dashed border-gray-500' 
                                  : dayPlan.length > 0 
                                    ? 'bg-white hover:bg-gray-100 cursor-pointer' 
                                    : 'bg-gray-200'
                              }`}
                              onClick={() => !isCurrentDay && dayPlan.length > 0 && copyWorkoutFromDay(weekIndex, dayIndex)}
                            >
                              <div className="flex-1">
                                <div className="flex items-center space-x-2">
                                  <span className="text-sm font-medium text-gray-700">{day}</span>
                                  {isCurrentDay && <span className="text-xs text-gray-500">(current)</span>}
                                </div>
                                {dayPlan.length > 0 ? (
                                  <div className="text-xs text-gray-500 mt-1">
                                    {dayPlan.map(ex => ex.name).join(', ')}
                                  </div>
                                ) : (
                                  <div className="text-xs text-gray-400 mt-1">No workout</div>
                                )}
                              </div>
                              {dayPlan.length > 0 && !isCurrentDay && (
                                <button className="w-8 h-8 bg-green-500 text-white rounded-full flex items-center justify-center hover:bg-green-600 transition-colors">
                                  <Plus className="w-4 h-4" />
                                </button>
                              )}
                            </div>
                          );
                        })}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default WorkoutApp;
