import React, { useState } from 'react';
import { Plus, Search, X, GripVertical, Trash2, ChevronDown, Target, Calendar, BarChart3, Dumbbell, Minus } from 'lucide-react';

const WorkoutApp = () => {
  const exerciseLibrary = [
    { id: 1, name: 'Bench Press', muscleGroups: ['Chest', 'Tri', 'Shoulders'], equipment: 'Barbell', tags: ['Compound', 'Push'], muscleWeights: [60, 25, 15] },
    { id: 2, name: 'Squat', muscleGroups: ['Legs', 'Core'], equipment: 'Barbell', tags: ['Compound', 'Legs'], muscleWeights: [85, 15] },
    { id: 3, name: 'Deadlift', muscleGroups: ['Legs', 'Back', 'Core'], equipment: 'Barbell', tags: ['Compound', 'Pull'], muscleWeights: [50, 35, 15] },
    { id: 4, name: 'Pull-ups', muscleGroups: ['Back', 'Bi'], equipment: 'Bodyweight', tags: ['Compound', 'Pull'], muscleWeights: [75, 25] },
    { id: 5, name: 'Overhead Press', muscleGroups: ['Shoulders', 'Tri', 'Core'], equipment: 'Barbell', tags: ['Compound', 'Push'], muscleWeights: [60, 30, 10] },
    { id: 6, name: 'Barbell Rows', muscleGroups: ['Back', 'Bi'], equipment: 'Barbell', tags: ['Compound', 'Pull'], muscleWeights: [70, 30] },
    { id: 7, name: 'Dips', muscleGroups: ['Tri', 'Chest', 'Shoulders'], equipment: 'Bodyweight', tags: ['Compound', 'Push'], muscleWeights: [50, 35, 15] },
    { id: 8, name: 'Lunges', muscleGroups: ['Legs', 'Core'], equipment: 'Bodyweight', tags: ['Compound', 'Legs'], muscleWeights: [85, 15] }
  ];

  const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
  const [currentDay, setCurrentDay] = useState(0);
  const [currentWeek, setCurrentWeek] = useState(0);
  const [currentTab, setCurrentTab] = useState('daily');
  const [showExerciseLibrary, setShowExerciseLibrary] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [dayNames] = useState(days.map(day => day));
  const [workoutPlans, setWorkoutPlans] = useState(
    Array(10).fill().map(() => days.map(() => []))
  );
  const [collapsedExercises, setCollapsedExercises] = useState({});
  const [showUpperLower, setShowUpperLower] = useState(false);
  const [libraryMode, setLibraryMode] = useState('exercises');

  const filteredExercises = exerciseLibrary.filter(exercise => {
    return exercise.name.toLowerCase().includes(searchTerm.toLowerCase());
  });

  const addExerciseDirectly = (exercise) => {
    const newExercise = {
      id: Date.now(),
      exerciseId: exercise.id,
      name: exercise.name,
      muscleGroups: exercise.muscleGroups,
      muscleWeights: exercise.muscleWeights,
      sets: 3,
      repsOrTime: 'reps',
      repsValue: 10,
      timeValue: 30,
      setResults: Array(3).fill().map(() => ({ weight: '', reps: '10' }))
    };

    const updatedPlans = [...workoutPlans];
    updatedPlans[currentWeek][currentDay] = [...updatedPlans[currentWeek][currentDay], newExercise];
    setWorkoutPlans(updatedPlans);
    setShowExerciseLibrary(false);
    setLibraryMode('exercises');
  };

  const copyWorkoutFromDay = (weekIndex, dayIndex) => {
    const sourceDayPlan = workoutPlans[weekIndex][dayIndex];
    if (sourceDayPlan.length === 0) return;

    const updatedPlans = [...workoutPlans];
    const copiedExercises = sourceDayPlan.map(exercise => ({
      ...exercise,
      id: Date.now() + Math.random(),
      setResults: exercise.setResults.map(set => ({ ...set }))
    }));

    updatedPlans[currentWeek][currentDay] = [
      ...updatedPlans[currentWeek][currentDay], 
      ...copiedExercises
    ];
    setWorkoutPlans(updatedPlans);
    setShowExerciseLibrary(false);
    setLibraryMode('exercises');
  };

  const MuscleGroupBar = ({ muscleGroups, muscleWeights }) => {
    const muscleColors = {
      'Chest': 'bg-red-500',
      'Tri': 'bg-orange-500',
      'Shoulders': 'bg-yellow-500',
      'Legs': 'bg-green-500',
      'Back': 'bg-blue-500',
      'Core': 'bg-purple-500',
      'Bi': 'bg-teal-500',
      'Upper': 'bg-blue-600',
      'Lower': 'bg-green-600',
      'Other': 'bg-gray-400'
    };

    return (
      <div className="w-full h-2 bg-gray-200 rounded-full overflow-hidden flex">
        {muscleGroups.map((muscle, index) => (
          <div
            key={muscle}
            className={`${muscleColors[muscle] || 'bg-gray-400'} h-full`}
            style={{ width: `${muscleWeights[index]}%` }}
            title={`${muscle}: ${muscleWeights[index]}%`}
          />
        ))}
      </div>
    );
  };

  const getUpperLowerDistribution = (exercises) => {
    const upperMuscles = ['Chest', 'Tri', 'Back', 'Bi', 'Shoulders'];
    const lowerMuscles = ['Legs'];

    const distribution = { upper: 0, lower: 0, core: 0 };
    let totalWeight = 0;

    exercises.forEach(exercise => {
      if (exercise.muscleGroups && exercise.muscleWeights) {
        exercise.muscleGroups.forEach((muscle, index) => {
          const weight = exercise.muscleWeights[index] || 0;
          totalWeight += weight;

          if (upperMuscles.includes(muscle)) {
            distribution.upper += weight;
          } else if (lowerMuscles.includes(muscle)) {
            distribution.lower += weight;
          } else if (muscle === 'Core') {
            distribution.core += weight;
          }
        });
      }
    });

    if (totalWeight === 0) return { upper: 0, lower: 0, core: 0 };

    let upperPercent = Math.round((distribution.upper / totalWeight) * 100);
    let lowerPercent = Math.round((distribution.lower / totalWeight) * 100);
    let corePercent = Math.round((distribution.core / totalWeight) * 100);

    const total = upperPercent + lowerPercent + corePercent;
    if (total !== 100 && total > 0) {
      const diff = 100 - total;
      if (upperPercent >= lowerPercent && upperPercent >= corePercent) {
        upperPercent += diff;
      } else if (lowerPercent >= corePercent) {
        lowerPercent += diff;
      } else {
        corePercent += diff;
      }
    }

    return {
      upper: upperPercent,
      lower: lowerPercent,
      core: corePercent
    };
  };

  const calculateDayMuscleDistribution = (exercises) => {
    const muscleTotal = {};
    let totalWeight = 0;

    exercises.forEach(exercise => {
      if (exercise.muscleGroups && exercise.muscleWeights) {
        exercise.muscleGroups.forEach((muscle, index) => {
          const weight = exercise.muscleWeights[index] || 0;
          muscleTotal[muscle] = (muscleTotal[muscle] || 0) + weight;
          totalWeight += weight;
        });
      }
    });

    if (totalWeight === 0) return { muscleGroups: [], muscleWeights: [] };

    const sortedMuscles = Object.entries(muscleTotal)
      .map(([muscle, weight]) => ({ 
        muscle, 
        weight: (weight / totalWeight) * 100
      }))
      .sort((a, b) => b.weight - a.weight);

    const top3 = sortedMuscles.slice(0, 3);
    const remaining = sortedMuscles.slice(3);

    const muscleGroups = top3.map(item => item.muscle);
    let muscleWeights = top3.map(item => item.weight);

    if (remaining.length > 0) {
      const otherWeight = remaining.reduce((sum, item) => sum + item.weight, 0);
      muscleGroups.push('Other');
      muscleWeights.push(otherWeight);
    }

    muscleWeights = muscleWeights.map(weight => Math.round(weight));

    const total = muscleWeights.reduce((sum, weight) => sum + weight, 0);
    if (total !== 100 && muscleWeights.length > 0) {
      const diff = 100 - total;
      const maxIndex = muscleWeights.indexOf(Math.max(...muscleWeights));
      muscleWeights[maxIndex] += diff;
    }

    return { muscleGroups, muscleWeights };
  };

  const removeExercise = (exerciseIndex) => {
    const updatedPlans = [...workoutPlans];
    updatedPlans[currentWeek][currentDay] = updatedPlans[currentWeek][currentDay].filter((_, index) => index !== exerciseIndex);
    setWorkoutPlans(updatedPlans);
  };

  const toggleExerciseCollapse = (exerciseId) => {
    setCollapsedExercises(prev => ({
      ...prev,
      [exerciseId]: !prev[exerciseId]
    }));
  };

  const updateExerciseSets = (exerciseIndex, change) => {
    const updatedPlans = [...workoutPlans];
    const exercise = updatedPlans[currentWeek][currentDay][exerciseIndex];
    const newSets = Math.max(1, exercise.sets + change);

    exercise.sets = newSets;

    if (newSets > exercise.setResults.length) {
      const additionalSets = newSets - exercise.setResults.length;
      const targetValue = exercise.repsOrTime === 'time' ? exercise.timeValue : exercise.repsValue;
      exercise.setResults = [
        ...exercise.setResults,
        ...Array(additionalSets).fill().map(() => ({ weight: '', reps: targetValue.toString() }))
      ];
    } else if (newSets < exercise.setResults.length) {
      exercise.setResults = exercise.setResults.slice(0, newSets);
    }

    setWorkoutPlans(updatedPlans);
  };

  const updateSetValue = (exerciseIndex, setIndex, field, value) => {
    const updatedPlans = [...workoutPlans];
    const exercise = updatedPlans[currentWeek][currentDay][exerciseIndex];

    if (!exercise.setResults[setIndex]) {
      exercise.setResults[setIndex] = { weight: '', reps: '' };
    }

    exercise.setResults[setIndex][field] = value;
    setWorkoutPlans(updatedPlans);
  };

  const WeeklySummaryView = () => (
    <div className="flex-1 overflow-y-auto pb-24 bg-gray-50">
      <div className="p-6">
        <h2 className="text-2xl font-bold text-gray-900 mb-6">Weekly Summary</h2>
        <div className="space-y-3">
          {Array.from({length: 10}, (_, weekIndex) => (
            <div key={weekIndex} className="bg-white rounded-xl shadow-sm border border-gray-100">
              <div className="p-5">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Week {weekIndex + 1}</h3>
                <div className="space-y-3">
                  {days.map((day, dayIndex) => {
                    const dayPlan = workoutPlans[weekIndex][dayIndex];
                    const { muscleGroups, muscleWeights } = calculateDayMuscleDistribution(dayPlan);

                    return (
                      <div key={dayIndex} className="bg-gray-50 rounded-lg p-3">
                        <div className="flex items-center justify-between mb-2">
                          <span className="text-sm font-medium text-gray-700">{dayNames[dayIndex]}</span>
                          <span className="text-xs text-gray-500">
                            {dayPlan.length} exercise{dayPlan.length !== 1 ? 's' : ''}
                          </span>
                        </div>

                        {muscleGroups.length > 0 && (
                          <div className="mb-2">
                            <MuscleGroupBar muscleGroups={muscleGroups} muscleWeights={muscleWeights} />
                          </div>
                        )}

                        {dayPlan.length > 0 ? (
                          <div className="space-y-1">
                            {dayPlan.map((exercise, idx) => (
                              <div key={idx} className="text-xs text-gray-600">
                                {exercise.name} - {exercise.sets} × {exercise.repsOrTime === 'reps' ? `${exercise.repsValue} reps` : `${exercise.timeValue} secs`}
                              </div>
                            ))}
                          </div>
                        ) : (
                          <div className="text-center py-2 text-gray-400">
                            <p className="text-xs">Rest day</p>
                          </div>
                        )}
                      </div>
                    );
                  })}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const ExerciseLibraryView = () => (
    <div className="flex-1 flex flex-col pb-24 bg-gray-50">
      <div className="p-6 bg-white border-b border-gray-100">
        <h2 className="text-2xl font-bold text-gray-900 mb-6">Exercise Library</h2>
        <div className="relative">
          <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
          <input
            type="text"
            placeholder="Search exercises..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full pl-12 pr-4 py-3 bg-gray-50 border-0 rounded-xl text-gray-900 placeholder-gray-500 focus:bg-white focus:ring-2 focus:ring-blue-500 transition-all"
          />
        </div>
      </div>

      <div className="flex-1 overflow-y-auto p-6">
        <div className="space-y-3">
          {filteredExercises.map(exercise => (
            <div
              key={exercise.id}
              className="bg-white rounded-xl p-4 shadow-sm border border-gray-100 flex items-center justify-between"
            >
              <div className="flex-1">
                <div className="font-semibold text-gray-900">{exercise.name}</div>
                {exercise.muscleWeights && (
                  <div className="mt-2 mb-1">
                    <MuscleGroupBar 
                      muscleGroups={exercise.muscleGroups} 
                      muscleWeights={exercise.muscleWeights} 
                    />
                  </div>
                )}
                <div className="text-sm text-gray-500 mt-1">{exercise.muscleGroups.join(', ')}</div>
                <div className="text-xs text-gray-400 mt-1">
                  {exercise.equipment} • {exercise.tags.join(', ')}
                </div>
              </div>
              <button
                onClick={() => addExerciseDirectly(exercise)}
                className="ml-4 w-10 h-10 bg-blue-500 text-white rounded-full flex items-center justify-center hover:bg-blue-600 transition-colors shadow-md"
              >
                <Plus className="w-5 h-5" />
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const DailyWorkoutView = () => (
    <div className="flex-1 flex flex-col bg-gray-50">
      <div className="bg-white px-4 py-4 border-b border-gray-100">
        <div className="flex space-x-2 overflow-x-auto mb-3">
          {Array.from({length: 10}, (_, index) => (
            <button
              key={index}
              onClick={() => setCurrentWeek(index)}
              className={`px-3 py-1.5 rounded-lg text-xs font-medium whitespace-nowrap transition-all ${
                currentWeek === index
                  ? 'bg-green-500 text-white shadow-md'
                  : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
              }`}
            >
              Week {index + 1}
            </button>
          ))}
        </div>
        <div className="flex space-x-2 overflow-x-auto">
          {days.map((day, index) => (
            <button
              key={index}
              onClick={() => setCurrentDay(index)}
              className={`px-4 py-2 rounded-xl text-sm font-medium whitespace-nowrap transition-all ${
                currentDay === index
                  ? 'bg-blue-500 text-white shadow-md'
                  : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
              }`}
            >
              {day.slice(0, 3)}
            </button>
          ))}
        </div>
      </div>

      <div className="p-6 bg-white border-b border-gray-100">
        <div className="text-2xl font-bold text-gray-900 flex items-center mb-1">
          {dayNames[currentDay]} <Calendar className="w-5 h-5 ml-2 text-gray-400" />
        </div>
        <div className="text-sm text-gray-500 mb-4">Week {currentWeek + 1}</div>

        {workoutPlans[currentWeek][currentDay].length > 0 && (
          <div className="mb-4">
            <div className="flex items-center justify-between mb-2">
              <span className="text-sm font-medium text-gray-700">
                {showUpperLower ? 'Upper/Lower Split' : 'Muscle Group Split'}
              </span>
              <div className="flex items-center space-x-2">
                <span className="text-xs text-gray-500">{workoutPlans[currentWeek][currentDay].length} exercises</span>
                <button
                  onClick={() => setShowUpperLower(!showUpperLower)}
                  className={`relative w-10 h-5 rounded-full transition-all duration-200 shadow-inner ${
                    showUpperLower ? 'bg-green-400' : 'bg-blue-500'
                  }`}
                >
                  <div
                    className={`absolute top-0.5 w-4 h-4 bg-white rounded-full transition-all duration-200 shadow-sm ${
                      showUpperLower ? 'translate-x-5' : 'translate-x-0.5'
                    }`}
                  />
                </button>
              </div>
            </div>
            {(() => {
              if (showUpperLower) {
                const { upper, lower, core } = getUpperLowerDistribution(workoutPlans[currentWeek][currentDay]);
                const groups = [];
                const weights = [];

                if (upper > 0) { groups.push('Upper'); weights.push(upper); }
                if (lower > 0) { groups.push('Lower'); weights.push(lower); }
                if (core > 0) { groups.push('Core'); weights.push(core); }

                return groups.length > 0 ? (
                  <>
                    <MuscleGroupBar muscleGroups={groups} muscleWeights={weights} />
                    <div className="flex flex-wrap gap-2 mt-2">
                      {groups.map((group, index) => (
                        <span 
                          key={group} 
                          className="text-xs bg-gray-100 px-2 py-1 rounded-full text-gray-700"
                        >
                          {group} {weights[index]}%
                        </span>
                      ))}
                    </div>
                  </>
                ) : null;
              } else {
                const { muscleGroups, muscleWeights } = calculateDayMuscleDistribution(workoutPlans[currentWeek][currentDay]);
                return muscleGroups.length > 0 ? (
                  <>
                    <MuscleGroupBar muscleGroups={muscleGroups} muscleWeights={muscleWeights} />
                    <div className="flex flex-wrap gap-2 mt-2">
                      {muscleGroups.map((muscle, index) => (
                        <span 
                          key={muscle} 
                          className="text-xs bg-gray-100 px-2 py-1 rounded-full text-gray-700"
                        >
                          {muscle} {muscleWeights[index]}%
                        </span>
                      ))}
                    </div>
                  </>
                ) : null;
              }
            })()}
          </div>
        )}

        <button
          onClick={() => setShowExerciseLibrary(true)}
          className="w-full bg-blue-500 text-white py-3 rounded-xl font-semibold flex items-center justify-center space-x-2 hover:bg-blue-600 transition-colors shadow-lg"
        >
          <Plus className="w-5 h-5" />
          <span>Add Exercise</span>
        </button>
      </div>

      <div className="flex-1 overflow-y-auto pb-24">
        {workoutPlans[currentWeek][currentDay].length === 0 ? (
          <div className="p-12 text-center text-gray-500">
            <div className="w-16 h-16 bg-gray-200 rounded-full mx-auto mb-4 flex items-center justify-center">
              <Target className="w-8 h-8 text-gray-400" />
            </div>
            <p className="text-xl font-medium mb-2 text-gray-700">No exercises planned</p>
            <p className="text-sm text-gray-500">Use the button above to add your first exercise</p>
          </div>
        ) : (
          <div className="p-6 space-y-4">
            {workoutPlans[currentWeek][currentDay].map((exercise, exerciseIndex) => {
              const isCollapsed = collapsedExercises[exercise.id];

              return (
                <div key={exercise.id} className="bg-white rounded-xl shadow-sm border border-gray-100 overflow-hidden">
                  <div className="flex items-center justify-between p-5">
                    <div className="flex items-center space-x-3">
                      <div className="p-1 text-gray-400">
                        <GripVertical className="w-4 h-4" />
                      </div>
                      <button
                        onClick={() => toggleExerciseCollapse(exercise.id)}
                        className="p-1 hover:bg-gray-100 rounded-lg transition-colors"
                      >
                        <ChevronDown className={`w-4 h-4 text-gray-400 transition-transform ${!isCollapsed ? 'rotate-180' : ''}`} />
                      </button>
                      <div>
                        <h3 className="text-lg font-semibold text-gray-900">{exercise.name}</h3>
                        {exercise.muscleWeights && (
                          <div className="mt-2 mb-1">
                            <MuscleGroupBar 
                              muscleGroups={exercise.muscleGroups} 
                              muscleWeights={exercise.muscleWeights} 
                            />
                          </div>
                        )}
                        <p className="text-sm text-gray-500 mt-1">{exercise.muscleGroups.join(', ')}</p>
                        {isCollapsed && (
                          <p className="text-sm text-gray-600 mt-1 font-medium">
                            {exercise.sets} × {exercise.repsOrTime === 'reps' ? `${exercise.repsValue} reps` : `${exercise.timeValue} secs`}
                          </p>
                        )}
                      </div>
                    </div>
                    <button
                      onClick={() => removeExercise(exerciseIndex)}
                      className="p-2 text-red-500 hover:bg-red-50 rounded-xl transition-colors"
                    >
                      <Trash2 className="w-4 h-4" />
                    </button>
                  </div>

                  {!isCollapsed && (
                    <div className="px-5 pb-5 border-t border-gray-50">
                      <div className="py-4 border-b border-gray-100">
                        <h4 className="text-sm font-medium text-gray-700 mb-4 underline">Target</h4>

                        <div className="mb-4">
                          <div className="flex items-center justify-between">
                            <span className="text-sm font-medium text-gray-700">Sets</span>
                            <div className="flex items-center space-x-4">
                              <button
                                onClick={() => updateExerciseSets(exerciseIndex, -1)}
                                className="w-8 h-8 bg-gray-100 rounded-full flex items-center justify-center hover:bg-gray-200 transition-colors"
                              >
                                <Minus className="w-4 h-4 text-gray-600" />
                              </button>
                              <span className="text-xl font-semibold text-gray-900 min-w-[2rem] text-center">{exercise.sets}</span>
                              <button
                                onClick={() => updateExerciseSets(exerciseIndex, 1)}
                                className="w-8 h-8 bg-gray-100 rounded-full flex items-center justify-center hover:bg-gray-200 transition-colors"
                              >
                                <Plus className="w-4 h-4 text-gray-600" />
                              </button>
                            </div>
                          </div>
                        </div>
                      </div>

                      <div className="space-y-3 px-4 pt-4">
                        {Array.from({ length: exercise.sets }, (_, setIndex) => {
                          const setResult = exercise.setResults?.[setIndex] || { weight: '', reps: '' };

                          return (
                            <div key={setIndex} className="p-3 bg-gray-50 rounded-xl relative">
                              <div className="absolute -top-2 -left-2 w-6 h-6 bg-blue-500 text-white rounded-full flex items-center justify-center text-xs font-bold border-2 border-white shadow-sm z-10">
                                {setIndex + 1}
                              </div>
                              <div className="flex items-center justify-between pl-4">
                                <div className="flex flex-col items-center">
                                  <span className="text-xs text-gray-500 mb-1 font-medium">Weight</span>
                                  <input
                                    type="text"
                                    value={setResult.weight || ''}
                                    onChange={(e) => updateSetValue(exerciseIndex, setIndex, 'weight', e.target.value)}
                                    placeholder="lbs"
                                    className="w-16 px-2 py-1.5 bg-white border-0 rounded-lg text-sm text-center font-medium text-gray-900 focus:ring-1 focus:ring-blue-500 shadow-sm"
                                  />
                                </div>

                                <span className="text-lg text-gray-400">×</span>

                                <div className="flex flex-col items-center">
                                  <span className="text-xs text-gray-500 mb-1 font-medium">Reps</span>
                                  <input
                                    type="text"
                                    value={setResult.reps || ''}
                                    onChange={(e) => updateSetValue(exerciseIndex, setIndex, 'reps', e.target.value)}
                                    placeholder="10"
                                    className="w-16 px-2 py-1.5 bg-white border-0 rounded-lg text-sm text-center font-medium text-gray-900 focus:ring-1 focus:ring-blue-500 shadow-sm"
                                  />
                                </div>
                              </div>
                            </div>
                          );
                        })}
                      </div>
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );

  return (
    <div className="max-w-md mx-auto bg-gray-50 min-h-screen flex flex-col">
      <div className="bg-white border-b border-gray-100 p-6 shadow-sm">
        <h1 className="text-2xl font-bold text-center text-gray-900">Workout Tracker</h1>
      </div>

      {currentTab === 'weekly' && <WeeklySummaryView />}
      {currentTab === 'library' && <ExerciseLibraryView />}
      {currentTab === 'daily' && <DailyWorkoutView />}

      <div className="fixed bottom-0 left-1/2 transform -translate-x-1/2 w-full max-w-md bg-white/95 backdrop-blur-md border-t border-gray-200 flex">
        <button
          onClick={() => setCurrentTab('weekly')}
          className={`flex-1 py-4 flex flex-col items-center space-y-1 transition-colors ${
            currentTab === 'weekly' ? 'text-blue-500' : 'text-gray-400'
          }`}
        >
          <BarChart3 className="w-5 h-5" />
          <span className="text-xs font-medium">Weekly</span>
        </button>
        <button
          onClick={() => setCurrentTab('library')}
          className={`flex-1 py-4 flex flex-col items-center space-y-1 transition-colors ${
            currentTab === 'library' ? 'text-blue-500' : 'text-gray-400'
          }`}
        >
          <Dumbbell className="w-5 h-5" />
          <span className="text-xs font-medium">Library</span>
        </button>
        <button
          onClick={() => setCurrentTab('daily')}
          className={`flex-1 py-4 flex flex-col items-center space-y-1 transition-colors ${
            currentTab === 'daily' ? 'text-blue-500' : 'text-gray-400'
          }`}
        >
          <Target className="w-5 h-5" />
          <span className="text-xs font-medium">Daily</span>
        </button>
      </div>

      {showExerciseLibrary && (
        <div className="fixed inset-0 bg-black/40 backdrop-blur-sm z-50 flex items-end">
          <div className="bg-white w-full h-4/5 rounded-t-3xl flex flex-col shadow-2xl">
            <div className="p-6 border-b border-gray-100 flex items-center justify-between">
              <h2 className="text-xl font-bold text-gray-900">
                {libraryMode === 'exercises' ? 'Exercise Library' : 'Copy from Previous Days'}
              </h2>
              <button
                onClick={() => {
                  setShowExerciseLibrary(false);
                  setLibraryMode('exercises');
                }}
                className="w-8 h-8 bg-gray-100 hover:bg-gray-200 rounded-full flex items-center justify-center transition-colors"
              >
                <X className="w-4 h-4 text-gray-600" />
              </button>
            </div>

            <div className="p-4 border-b border-gray-100">
              <div className="flex space-x-2">
                <button
                  onClick={() => setLibraryMode('exercises')}
                  className={`flex-1 py-2 px-4 rounded-xl text-sm font-medium transition-all ${
                    libraryMode === 'exercises'
                      ? 'bg-blue-500 text-white'
                      : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                  }`}
                >
                  Add New Exercises
                </button>
                <button
                  onClick={() => setLibraryMode('copy')}
                  className={`flex-1 py-2 px-4 rounded-xl text-sm font-medium transition-all ${
                    libraryMode === 'copy'
                      ? 'bg-green-500 text-white'
                      : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                  }`}
                >
                  Copy from Past Days
                </button>
              </div>
            </div>

            <div className="flex-1 overflow-y-auto p-6">
              {libraryMode === 'exercises' ? (
                <div className="space-y-3">
                  {filteredExercises.map(exercise => (
                    <div
                      key={exercise.id}
                      className="bg-gray-50 rounded-xl p-4 flex items-center justify-between hover:bg-gray-100 transition-colors"
                    >
                      <div className="flex-1">
                        <div className="font-semibold text-gray-900">{exercise.name}</div>
                        {exercise.muscleWeights && (
                          <div className="mt-2 mb-1">
                            <MuscleGroupBar 
                              muscleGroups={exercise.muscleGroups} 
                              muscleWeights={exercise.muscleWeights} 
                            />
                          </div>
                        )}
                        <div className="text-sm text-gray-500 mt-1">{exercise.muscleGroups.join(', ')}</div>
                        <div className="text-xs text-gray-400 mt-2">
                          {exercise.equipment} • {exercise.tags.join(', ')}
                        </div>
                      </div>
                      <button
                        onClick={() => addExerciseDirectly(exercise)}
                        className="ml-4 w-10 h-10 bg-blue-500 text-white rounded-full flex items-center justify-center hover:bg-blue-600 transition-colors shadow-md"
                      >
                        <Plus className="w-5 h-5" />
                      </button>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="space-y-4">
                  {Array.from({length: 10}, (_, weekIndex) => (
                    <div key={weekIndex} className="bg-gray-50 rounded-xl p-4">
                      <h3 className="text-md font-semibold text-gray-900 mb-3">Week {weekIndex + 1}</h3>
                      <div className="space-y-2">
                        {days.map((day, dayIndex) => {
                          const dayPlan = workoutPlans[weekIndex][dayIndex];
                          const isCurrentDay = weekIndex === currentWeek && dayIndex === currentDay;

                          return (
                            <div
                              key={dayIndex}
                              className={`p-3 rounded-lg flex items-center justify-between ${
                                isCurrentDay 
                                  ? 'bg-gray-300 border-2 border-dashed border-gray-500' 
                                  : dayPlan.length > 0 
                                    ? 'bg-white hover:bg-gray-100 cursor-pointer' 
                                    : 'bg-gray-200'
                              }`}
                              onClick={() => !isCurrentDay && dayPlan.length > 0 && copyWorkoutFromDay(weekIndex, dayIndex)}
                            >
                              <div className="flex-1">
                                <div className="flex items-center space-x-2">
                                  <span className="text-sm font-medium text-gray-700">{day}</span>
                                  {isCurrentDay && <span className="text-xs text-gray-500">(current)</span>}
                                </div>
                                {dayPlan.length > 0 ? (
                                  <div className="text-xs text-gray-500 mt-1">
                                    {dayPlan.map(ex => ex.name).join(', ')}
                                  </div>
                                ) : (
                                  <div className="text-xs text-gray-400 mt-1">No workout</div>
                                )}
                              </div>
                              {dayPlan.length > 0 && !isCurrentDay && (
                                <button className="w-8 h-8 bg-green-500 text-white rounded-full flex items-center justify-center hover:bg-green-600 transition-colors">
                                  <Plus className="w-4 h-4" />
                                </button>
                              )}
                            </div>
                          );
                        })}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default WorkoutApp;
