import React, { useState, useEffect } from 'react';
import { Plus, Search, X, GripVertical, Trash2, ChevronDown, ChevronUp, Target, Calendar, BarChart3, Dumbbell, Minus } from 'lucide-react';

const WorkoutApp = () => {
  const exerciseLibrary = [
    { id: 1, name: 'Bench Press', muscleGroups: ['Chest', 'Triceps', 'Shoulders'], equipment: 'Barbell', tags: ['Compound', 'Push'], muscleWeights: [60, 25, 15] },
    { id: 2, name: 'Squat', muscleGroups: ['Quads', 'Glutes', 'Core'], equipment: 'Barbell', tags: ['Compound', 'Legs'], muscleWeights: [50, 35, 15] },
    { id: 3, name: 'Deadlift', muscleGroups: ['Hamstrings', 'Glutes', 'Back', 'Core'], equipment: 'Barbell', tags: ['Compound', 'Pull'], muscleWeights: [35, 30, 25, 10] },
    { id: 4, name: 'Pull-ups', muscleGroups: ['Lats', 'Biceps', 'Rear Delts'], equipment: 'Bodyweight', tags: ['Compound', 'Pull'], muscleWeights: [65, 20, 15] },
    { id: 5, name: 'Overhead Press', muscleGroups: ['Shoulders', 'Triceps', 'Core'], equipment: 'Barbell', tags: ['Compound', 'Push'], muscleWeights: [60, 30, 10] }
  ];

  const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
  const [currentDay, setCurrentDay] = useState(0);
  const [currentTab, setCurrentTab] = useState('daily');
  const [showExerciseLibrary, setShowExerciseLibrary] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterMuscleGroup, setFilterMuscleGroup] = useState('');
  const [dayNames, setDayNames] = useState(days.map(day => day));
  const [editingDayName, setEditingDayName] = useState(null);
  const [workoutPlans, setWorkoutPlans] = useState(days.map(() => []));
  const [workoutHistory, setWorkoutHistory] = useState({});
  const [dailyNotes, setDailyNotes] = useState(days.map(() => ''));
  const [collapsedExercises, setCollapsedExercises] = useState({});
  const [collapsedWeeklyDays, setCollapsedWeeklyDays] = useState({});
  const [draggedItem, setDraggedItem] = useState(null);
  const [dragOverIndex, setDragOverIndex] = useState(null);

  useEffect(() => {
    // No jiggle animation needed anymore
  }, []);

  const handleDragStart = (e, exerciseIndex) => {
    setDraggedItem(exerciseIndex);
    e.dataTransfer.effectAllowed = 'move';
    e.dataTransfer.setData('text/plain', exerciseIndex.toString());
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';
  };

  const handleDragEnter = (e, index) => {
    e.preventDefault();
    if (draggedItem !== null && draggedItem !== index) {
      setDragOverIndex(index);
    }
  };

  const handleDrop = (e, dropIndex) => {
    e.preventDefault();
    e.stopPropagation();

    const dragIndex = parseInt(e.dataTransfer.getData('text/plain'));

    if (dragIndex === dropIndex || isNaN(dragIndex)) {
      setDraggedItem(null);
      setDragOverIndex(null);
      return;
    }

    const updatedPlans = [...workoutPlans];
    const exercises = [...updatedPlans[currentDay]];

    // Remove the dragged exercise
    const [draggedExercise] = exercises.splice(dragIndex, 1);

    // Adjust drop index if dragging from earlier position
    const adjustedDropIndex = dragIndex < dropIndex ? dropIndex - 1 : dropIndex;

    // Insert at the new position
    exercises.splice(adjustedDropIndex, 0, draggedExercise);

    updatedPlans[currentDay] = exercises;
    setWorkoutPlans(updatedPlans);

    setDraggedItem(null);
    setDragOverIndex(null);
  };

  const handleDragEnd = () => {
    setDraggedItem(null);
    setDragOverIndex(null);
  };

  const muscleGroups = [...new Set(exerciseLibrary.flatMap(ex => ex.muscleGroups))];

  const filteredExercises = exerciseLibrary.filter(exercise => {
    const matchesSearch = exercise.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesMuscleGroup = !filterMuscleGroup || exercise.muscleGroups.includes(filterMuscleGroup);
    return matchesSearch && matchesMuscleGroup;
  });

  const addExerciseDirectly = (exercise) => {
    const newExercise = {
      id: Date.now(),
      exerciseId: exercise.id,
      name: exercise.name,
      muscleGroups: exercise.muscleGroups,
      muscleWeights: exercise.muscleWeights,
      sets: 3,
      repsOrTime: 'reps',
      repsValue: 10,
      timeValue: 30,
      setResults: Array(3).fill().map(() => ({ weight: '', reps: '10' }))
    };

    const updatedPlans = [...workoutPlans];
    updatedPlans[currentDay] = [...updatedPlans[currentDay], newExercise];
    setWorkoutPlans(updatedPlans);
    setShowExerciseLibrary(false);
  };

  const MuscleGroupBar = ({ muscleGroups, muscleWeights }) => {
    const muscleColors = {
      'Chest': 'bg-red-500',
      'Triceps': 'bg-orange-500',
      'Shoulders': 'bg-yellow-500',
      'Quads': 'bg-green-500',
      'Glutes': 'bg-blue-500',
      'Core': 'bg-purple-500',
      'Hamstrings': 'bg-pink-500',
      'Back': 'bg-indigo-500',
      'Lats': 'bg-cyan-500',
      'Biceps': 'bg-teal-500',
      'Rear Delts': 'bg-amber-500'
    };

    return (
      <div className="w-full h-2 bg-gray-200 rounded-full overflow-hidden flex">
        {muscleGroups.map((muscle, index) => (
          <div
            key={muscle}
            className={`${muscleColors[muscle] || 'bg-gray-400'} h-full`}
            style={{ width: `${muscleWeights[index]}%` }}
            title={`${muscle}: ${muscleWeights[index]}%`}
          />
        ))}
      </div>
    );
  };

  const calculateDayMuscleDistribution = (exercises) => {
    const muscleTotal = {};
    let totalWeight = 0;

    exercises.forEach(exercise => {
      if (exercise.muscleGroups && exercise.muscleWeights) {
        exercise.muscleGroups.forEach((muscle, index) => {
          const weight = exercise.muscleWeights[index] || 0;
          muscleTotal[muscle] = (muscleTotal[muscle] || 0) + weight;
          totalWeight += weight;
        });
      }
    });

    if (totalWeight === 0) return { muscleGroups: [], muscleWeights: [] };

    // Convert to percentages
    const muscleGroups = Object.keys(muscleTotal);
    const muscleWeights = muscleGroups.map(muscle => 
      Math.round((muscleTotal[muscle] / totalWeight) * 100)
    );

    // Ensure total is 100% (handle rounding)
    const sum = muscleWeights.reduce((a, b) => a + b, 0);
    if (sum !== 100 && muscleWeights.length > 0) {
      muscleWeights[0] += 100 - sum;
    }

    return { muscleGroups, muscleWeights };
  };

  const removeExercise = (exerciseIndex) => {
    const updatedPlans = [...workoutPlans];
    updatedPlans[currentDay] = updatedPlans[currentDay].filter((_, index) => index !== exerciseIndex);
    setWorkoutPlans(updatedPlans);
  };

  const toggleExerciseCollapse = (exerciseId) => {
    setCollapsedExercises(prev => ({
      ...prev,
      [exerciseId]: !prev[exerciseId]
    }));
  };

  const updateExerciseSets = (exerciseIndex, change) => {
    const updatedPlans = [...workoutPlans];
    const exercise = updatedPlans[currentDay][exerciseIndex];
    const newSets = Math.max(1, exercise.sets + change);

    exercise.sets = newSets;

    if (newSets > exercise.setResults.length) {
      const additionalSets = newSets - exercise.setResults.length;
      const targetValue = exercise.repsOrTime === 'time' ? exercise.timeValue : exercise.repsValue;
      exercise.setResults = [
        ...exercise.setResults,
        ...Array(additionalSets).fill().map(() => ({ weight: '', reps: targetValue.toString() }))
      ];
    } else if (newSets < exercise.setResults.length) {
      exercise.setResults = exercise.setResults.slice(0, newSets);
    }

    setWorkoutPlans(updatedPlans);
  };

  const updateExerciseReps = (exerciseIndex, change) => {
    const updatedPlans = [...workoutPlans];
    const exercise = updatedPlans[currentDay][exerciseIndex];

    if (exercise.repsOrTime === 'time') {
      const newValue = Math.max(5, exercise.timeValue + (change * 5));
      exercise.timeValue = newValue;
    } else {
      const newValue = Math.max(1, exercise.repsValue + change);
      exercise.repsValue = newValue;
    }

    // Update all existing set results to the new target value
    const targetValue = exercise.repsOrTime === 'time' ? exercise.timeValue : exercise.repsValue;
    exercise.setResults.forEach(setResult => {
      if (!setResult.reps || setResult.reps === '') {
        setResult.reps = targetValue.toString();
      }
    });

    setWorkoutPlans(updatedPlans);
  };

  const toggleExerciseMode = (exerciseIndex) => {
    const updatedPlans = [...workoutPlans];
    const exercise = updatedPlans[currentDay][exerciseIndex];
    exercise.repsOrTime = exercise.repsOrTime === 'time' ? 'reps' : 'time';

    // Update all existing set results to the new target value
    const targetValue = exercise.repsOrTime === 'time' ? exercise.timeValue : exercise.repsValue;
    exercise.setResults.forEach(setResult => {
      if (!setResult.reps || setResult.reps === '') {
        setResult.reps = targetValue.toString();
      }
    });

    setWorkoutPlans(updatedPlans);
  };

  const updateSetValue = (exerciseIndex, setIndex, field, value, increment = 0) => {
    const updatedPlans = [...workoutPlans];
    const exercise = updatedPlans[currentDay][exerciseIndex];

    if (!exercise.setResults[setIndex]) {
      exercise.setResults[setIndex] = { weight: '', reps: '' };
    }

    if (increment !== 0) {
      const currentValue = parseInt(exercise.setResults[setIndex][field]) || 0;
      const isTime = exercise.repsOrTime === 'time';
      const incrementValue = isTime ? 5 : 1;
      const newValue = Math.max(0, currentValue + (increment * incrementValue));
      exercise.setResults[setIndex][field] = newValue.toString();
    } else if (value === 'default') {
      // Set to target value when user wants default
      const targetValue = exercise.repsOrTime === 'time' ? exercise.timeValue : exercise.repsValue;
      exercise.setResults[setIndex][field] = targetValue.toString();
    } else {
      exercise.setResults[setIndex][field] = value;
    }

    setWorkoutPlans(updatedPlans);
  };

  const toggleSetMode = (exerciseIndex, setIndex) => {
    const updatedPlans = [...workoutPlans];
    const exercise = updatedPlans[currentDay][exerciseIndex];

    if (!exercise.setResults[setIndex]) {
      exercise.setResults[setIndex] = { weight: '', reps: '', isTime: false, isKg: false };
    }

    exercise.setResults[setIndex].isTime = !exercise.setResults[setIndex].isTime;
    exercise.setResults[setIndex].reps = '';
    setWorkoutPlans(updatedPlans);
  };

  const toggleWeightUnit = (exerciseIndex, setIndex) => {
    const updatedPlans = [...workoutPlans];
    const exercise = updatedPlans[currentDay][exerciseIndex];

    if (!exercise.setResults[setIndex]) {
      exercise.setResults[setIndex] = { weight: '', reps: '', isTime: false, isKg: false };
    }

    exercise.setResults[setIndex].isKg = !exercise.setResults[setIndex].isKg;
    setWorkoutPlans(updatedPlans);
  };

  const WeeklySummaryView = () => (
    <div className="flex-1 overflow-y-auto pb-24 bg-gray-50">
      <div className="p-6">
        <h2 className="text-2xl font-bold text-gray-900 mb-6">Weekly Summary</h2>
        <div className="space-y-3">
          {days.map((day, dayIndex) => (
            <div key={dayIndex} className="bg-white rounded-xl shadow-sm border border-gray-100">
              <div className="p-5">
                <h3 className="text-lg font-semibold text-gray-900">{dayNames[dayIndex]}</h3>
                <p className="text-sm text-gray-500 mt-1">
                  {workoutPlans[dayIndex].length} exercise{workoutPlans[dayIndex].length !== 1 ? 's' : ''}
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const ExerciseLibraryView = () => (
    <div className="flex-1 flex flex-col pb-24 bg-gray-50">
      <div className="p-6 bg-white border-b border-gray-100">
        <h2 className="text-2xl font-bold text-gray-900 mb-6">Exercise Library</h2>
        <div className="space-y-4">
          <div className="relative">
            <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
            <input
              type="text"
              placeholder="Search exercises..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-12 pr-4 py-3 bg-gray-50 border-0 rounded-xl text-gray-900 placeholder-gray-500 focus:bg-white focus:ring-2 focus:ring-blue-500 transition-all"
            />
          </div>
        </div>
      </div>

      <div className="flex-1 overflow-y-auto p-6">
        <div className="space-y-3">
          {filteredExercises.map(exercise => (
            <div
              key={exercise.id}
              className="bg-white rounded-xl p-4 shadow-sm border border-gray-100 flex items-center justify-between"
            >
              <div className="flex-1">
                <div className="font-semibold text-gray-900">{exercise.name}</div>
                {exercise.muscleWeights && (
                  <div className="mt-2 mb-1">
                    <MuscleGroupBar 
                      muscleGroups={exercise.muscleGroups} 
                      muscleWeights={exercise.muscleWeights} 
                    />
                  </div>
                )}
                <div className="text-sm text-gray-500 mt-1">{exercise.muscleGroups.join(', ')}</div>
              </div>
              <button
                onClick={() => addExerciseDirectly(exercise)}
                className="ml-4 w-10 h-10 bg-blue-500 text-white rounded-full flex items-center justify-center hover:bg-blue-600 transition-colors shadow-md"
              >
                <Plus className="w-5 h-5" />
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const DailyWorkoutView = () => (
    <div className="flex-1 flex flex-col bg-gray-50">
      <div className="bg-white px-4 py-4 border-b border-gray-100">
        <div className="flex space-x-2 overflow-x-auto">
          {days.map((day, index) => (
            <button
              key={index}
              onClick={() => setCurrentDay(index)}
              className={`px-4 py-2 rounded-xl text-sm font-medium whitespace-nowrap transition-all ${
                currentDay === index
                  ? 'bg-blue-500 text-white shadow-md'
                  : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
              }`}
            >
              {day.slice(0, 3)}
            </button>
          ))}
        </div>
      </div>

      <div className="p-6 bg-white border-b border-gray-100">
        <button
          onClick={() => setEditingDayName(currentDay)}
          className="text-2xl font-bold text-gray-900 hover:text-blue-600 transition-colors flex items-center mb-4"
        >
          {dayNames[currentDay]} <Calendar className="w-5 h-5 ml-2 text-gray-400" />
        </button>

        {/* Daily Muscle Distribution Summary */}
        {workoutPlans[currentDay].length > 0 && (
          <div className="mb-4">
            <div className="flex items-center justify-between mb-2">
              <span className="text-sm font-medium text-gray-700">Muscle Focus</span>
              <span className="text-xs text-gray-500">{workoutPlans[currentDay].length} exercises</span>
            </div>
            {(() => {
              const { muscleGroups, muscleWeights } = calculateDayMuscleDistribution(workoutPlans[currentDay]);
              return muscleGroups.length > 0 ? (
                <>
                  <MuscleGroupBar muscleGroups={muscleGroups} muscleWeights={muscleWeights} />
                  <div className="flex flex-wrap gap-2 mt-2">
                    {muscleGroups.map((muscle, index) => (
                      <span 
                        key={muscle} 
                        className="text-xs bg-gray-100 px-2 py-1 rounded-full text-gray-700"
                      >
                        {muscle} {muscleWeights[index]}%
                      </span>
                    ))}
                  </div>
                </>
              ) : null;
            })()}
          </div>
        )}

        <button
          onClick={() => setShowExerciseLibrary(true)}
          className="w-full bg-blue-500 text-white py-3 rounded-xl font-semibold flex items-center justify-center space-x-2 hover:bg-blue-600 transition-colors shadow-lg"
        >
          <Plus className="w-5 h-5" />
          <span>Add Exercise</span>
        </button>
      </div>

      <div className="flex-1 overflow-y-auto pb-24">
        {workoutPlans[currentDay].length === 0 ? (
          <div className="p-12 text-center text-gray-500">
            <div className="w-16 h-16 bg-gray-200 rounded-full mx-auto mb-4 flex items-center justify-center">
              <Target className="w-8 h-8 text-gray-400" />
            </div>
            <p className="text-xl font-medium mb-2 text-gray-700">No exercises planned</p>
            <p className="text-sm text-gray-500">Use the button above to add your first exercise</p>
          </div>
        ) : (
          <div className="p-6 space-y-4">
            {workoutPlans[currentDay].map((exercise, exerciseIndex) => {
              const isCollapsed = collapsedExercises[exercise.id];

              return (
                <div 
                  key={exercise.id} 
                  className={`bg-white rounded-xl shadow-sm border border-gray-100 overflow-hidden transition-all duration-200 ${
                    draggedItem === exerciseIndex ? 'opacity-50 scale-105' : ''
                  } ${dragOverIndex === exerciseIndex && draggedItem !== exerciseIndex ? 'border-blue-400 bg-blue-50 transform translate-y-1' : ''}`}
                  draggable={true}
                  onDragStart={(e) => handleDragStart(e, exerciseIndex)}
                  onDragOver={handleDragOver}
                  onDragEnter={(e) => handleDragEnter(e, exerciseIndex)}
                  onDragEnd={handleDragEnd}
                  onDrop={(e) => handleDrop(e, exerciseIndex)}
                >
                  <div className="flex items-center justify-between p-5">
                    <div className="flex items-center space-x-3">
                      <div className="p-1 text-gray-400 cursor-grab active:cursor-grabbing">
                        <GripVertical className="w-4 h-4" />
                      </div>
                      <button
                        onClick={() => toggleExerciseCollapse(exercise.id)}
                        className="p-1 hover:bg-gray-100 rounded-lg transition-colors"
                      >
                        <ChevronDown className={`w-4 h-4 text-gray-400 transition-transform ${!isCollapsed ? 'rotate-180' : ''}`} />
                      </button>
                      <div>
                        <h3 className="text-lg font-semibold text-gray-900">{exercise.name}</h3>
                        {exercise.muscleWeights && (
                          <div className="mt-2 mb-1">
                            <MuscleGroupBar 
                              muscleGroups={exercise.muscleGroups} 
                              muscleWeights={exercise.muscleWeights} 
                            />
                          </div>
                        )}
                        <p className="text-sm text-gray-500 mt-1">{exercise.muscleGroups.join(', ')}</p>
                        {isCollapsed && (
                          <p className="text-sm text-gray-600 mt-1 font-medium">
                            {exercise.sets}  {exercise.repsOrTime === 'reps' ? `${exercise.repsValue} reps` : `${exercise.timeValue} secs`}
                          </p>
                        )}
                      </div>
                    </div>
                    <button
                      onClick={() => removeExercise(exerciseIndex)}
                      className="p-2 text-red-500 hover:bg-red-50 rounded-xl transition-colors"
                    >
                      <Trash2 className="w-4 h-4" />
                    </button>
                  </div>

                  {!isCollapsed && (
                    <div className="px-5 pb-5 border-t border-gray-50">
                      <div className="py-4 border-b border-gray-100">
                        <h4 className="text-sm font-medium text-gray-700 mb-4 underline">Target</h4>

                        <div className="mb-4">
                          <div className="flex items-center justify-between">
                            <span className="text-sm font-medium text-gray-700">Sets</span>
                            <div className="flex items-center space-x-4">
                              <button
                                onClick={() => updateExerciseSets(exerciseIndex, -1)}
                                className="w-8 h-8 bg-gray-100 rounded-full flex items-center justify-center hover:bg-gray-200 transition-colors"
                              >
                                <Minus className="w-4 h-4 text-gray-600" />
                              </button>
                              <span className="text-xl font-semibold text-gray-900 min-w-[2rem] text-center">{exercise.sets}</span>
                              <button
                                onClick={() => updateExerciseSets(exerciseIndex, 1)}
                                className="w-8 h-8 bg-gray-100 rounded-full flex items-center justify-center hover:bg-gray-200 transition-colors"
                              >
                                <Plus className="w-4 h-4 text-gray-600" />
                              </button>
                            </div>
                          </div>
                        </div>

                        <div>
                          <div className="flex items-center justify-between">
                            <div className="flex items-center space-x-2">
                              <span className="text-sm font-medium text-gray-700">
                                {exercise.repsOrTime === 'time' ? 'Secs' : 'Reps'}
                              </span>
                              <button
                                onClick={() => toggleExerciseMode(exerciseIndex)}
                                className={`relative w-10 h-5 rounded-full transition-all duration-200 shadow-inner ${
                                  exercise.repsOrTime === 'time' ? 'bg-orange-400' : 'bg-blue-500'
                                }`}
                              >
                                <div
                                  className={`absolute top-0.5 w-4 h-4 bg-white rounded-full transition-all duration-200 shadow-sm ${
                                    exercise.repsOrTime === 'time' ? 'translate-x-5' : 'translate-x-0.5'
                                  }`}
                                />
                              </button>
                            </div>
                            <div className="flex items-center space-x-4">
                              <button
                                onClick={() => updateExerciseReps(exerciseIndex, -1)}
                                className="w-8 h-8 bg-gray-100 rounded-full flex items-center justify-center hover:bg-gray-200 transition-colors"
                              >
                                <Minus className="w-4 h-4 text-gray-600" />
                              </button>
                              <span className="text-xl font-semibold text-gray-900 min-w-[2rem] text-center">
                                {exercise.repsOrTime === 'time' ? exercise.timeValue : exercise.repsValue}
                              </span>
                              <button
                                onClick={() => updateExerciseReps(exerciseIndex, 1)}
                                className="w-8 h-8 bg-gray-100 rounded-full flex items-center justify-center hover:bg-gray-200 transition-colors"
                              >
                                <Plus className="w-4 h-4 text-gray-600" />
                              </button>
                            </div>
                          </div>
                        </div>
                      </div>

                      <div className="space-y-3 px-4 pt-4">
                        {Array.from({ length: exercise.sets }, (_, setIndex) => {
                          const setResult = exercise.setResults?.[setIndex] || { weight: '', reps: '' };

                          return (
                            <div key={setIndex} className="p-3 bg-gray-50 rounded-xl relative">
                              <div className="absolute -top-2 -left-2 w-6 h-6 bg-blue-500 text-white rounded-full flex items-center justify-center text-xs font-bold border-2 border-white shadow-sm z-10">
                                {setIndex + 1}
                              </div>
                              <div className="flex items-center justify-between pl-4">

                                <div className="flex flex-col items-center">
                                  <span className="text-xs text-gray-500 mb-1 font-medium">lb</span>
                                  <select
                                    value={setResult.weight || ''}
                                    onChange={(e) => updateSetValue(exerciseIndex, setIndex, 'weight', e.target.value)}
                                    className="w-20 px-2 py-1.5 bg-white border-0 rounded-lg text-sm text-center font-medium text-gray-900 focus:ring-1 focus:ring-blue-500 appearance-none shadow-sm"
                                  >
                                    <option value=""></option>
                                    <option value="bodyweight">BW</option>
                                    {Array.from({length: 100}, (_, i) => i + 1).map(num => (
                                      <option key={num} value={num}>{num}</option>
                                    ))}
                                  </select>
                                </div>

                                <span className="text-lg text-gray-400"></span>

                                <div className="flex flex-col items-center">
                                  <span className="text-xs text-gray-500 mb-1 font-medium">
                                    {exercise.repsOrTime === 'time' ? 'secs' : 'reps'}
                                  </span>

                                  <div className="flex items-center space-x-1">
                                    <button
                                      onClick={() => updateSetValue(exerciseIndex, setIndex, 'reps', '', -1)}
                                      className="w-6 h-6 bg-white rounded-full flex items-center justify-center hover:bg-gray-100 transition-colors shadow-sm"
                                    >
                                      <Minus className="w-2.5 h-2.5 text-gray-600" />
                                    </button>
                                    <input
                                      type="text"
                                      value={setResult.reps || ''}
                                      className="w-12 px-2 py-1.5 bg-white border-0 rounded-lg text-sm text-center font-medium text-gray-900 focus:ring-1 focus:ring-blue-500 shadow-sm"
                                      onChange={(e) => updateSetValue(exerciseIndex, setIndex, 'reps', e.target.value)}
                                    />
                                    <button
                                      onClick={() => updateSetValue(exerciseIndex, setIndex, 'reps', '', 1)}
                                      className="w-6 h-6 bg-white rounded-full flex items-center justify-center hover:bg-gray-100 transition-colors shadow-sm"
                                    >
                                      <Plus className="w-2.5 h-2.5 text-gray-600" />
                                    </button>
                                  </div>
                                </div>
                              </div>
                            </div>
                          );
                        })}
                      </div>
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );

  return (
    <div className="max-w-md mx-auto bg-gray-50 min-h-screen flex flex-col">
      <div className="bg-white border-b border-gray-100 p-6 shadow-sm">
        <h1 className="text-2xl font-bold text-center text-gray-900">Workout Tracker</h1>
      </div>

      {currentTab === 'weekly' && <WeeklySummaryView />}
      {currentTab === 'library' && <ExerciseLibraryView />}
      {currentTab === 'daily' && <DailyWorkoutView />}

      <div className="fixed bottom-0 left-1/2 transform -translate-x-1/2 w-full max-w-md bg-white/95 backdrop-blur-md border-t border-gray-200 flex">
        <button
          onClick={() => setCurrentTab('weekly')}
          className={`flex-1 py-4 flex flex-col items-center space-y-1 transition-colors ${
            currentTab === 'weekly' ? 'text-blue-500' : 'text-gray-400'
          }`}
        >
          <BarChart3 className="w-5 h-5" />
          <span className="text-xs font-medium">Weekly</span>
        </button>
        <button
          onClick={() => setCurrentTab('library')}
          className={`flex-1 py-4 flex flex-col items-center space-y-1 transition-colors ${
            currentTab === 'library' ? 'text-blue-500' : 'text-gray-400'
          }`}
        >
          <Dumbbell className="w-5 h-5" />
          <span className="text-xs font-medium">Library</span>
        </button>
        <button
          onClick={() => setCurrentTab('daily')}
          className={`flex-1 py-4 flex flex-col items-center space-y-1 transition-colors ${
            currentTab === 'daily' ? 'text-blue-500' : 'text-gray-400'
          }`}
        >
          <Target className="w-5 h-5" />
          <span className="text-xs font-medium">Daily</span>
        </button>
      </div>

      {showExerciseLibrary && (
        <div className="fixed inset-0 bg-black/40 backdrop-blur-sm z-50 flex items-end">
          <div className="bg-white w-full h-4/5 rounded-t-3xl flex flex-col shadow-2xl">
            <div className="p-6 border-b border-gray-100 flex items-center justify-between">
              <h2 className="text-xl font-bold text-gray-900">Exercise Library</h2>
              <button
                onClick={() => setShowExerciseLibrary(false)}
                className="w-8 h-8 bg-gray-100 hover:bg-gray-200 rounded-full flex items-center justify-center transition-colors"
              >
                <X className="w-4 h-4 text-gray-600" />
              </button>
            </div>

            <div className="flex-1 overflow-y-auto p-6">
              <div className="space-y-3">
                {filteredExercises.map(exercise => (
                  <div
                    key={exercise.id}
                    className="bg-gray-50 rounded-xl p-4 flex items-center justify-between hover:bg-gray-100 transition-colors"
                  >
                    <div className="flex-1">
                      <div className="font-semibold text-gray-900">{exercise.name}</div>
                      {exercise.muscleWeights && (
                        <div className="mt-2 mb-1">
                          <MuscleGroupBar 
                            muscleGroups={exercise.muscleGroups} 
                            muscleWeights={exercise.muscleWeights} 
                          />
                        </div>
                      )}
                      <div className="text-sm text-gray-500 mt-1">{exercise.muscleGroups.join(', ')}</div>
                      <div className="text-xs text-gray-400 mt-2">
                        {exercise.equipment}  {exercise.tags.join(', ')}
                      </div>
                    </div>
                    <button
                      onClick={() => addExerciseDirectly(exercise)}
                      className="ml-4 w-10 h-10 bg-blue-500 text-white rounded-full flex items-center justify-center hover:bg-blue-600 transition-colors shadow-md"
                    >
                      <Plus className="w-5 h-5" />
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default WorkoutApp;
